  const [searchquery, setsearchquery] = useState("");
  let cardsData = ["hello", "world"];
  const searchQuery = ()=>{
      cardsData.forEach((element, i)=>{
          if(element === searchquery)
          {
            console.log(i);
          }
      })
  }
  return (
    <div className="App">
      <div className="search-bar">
        PokeMon Cards
