import './PokeCard.css'
import{useEffect, useState} from 'react';

export default function PokeCard(props){
    const name = props.name;
    const hp = props.hp;
    const imagelink = props.imagelink;
    const abilities = props.abilities;
    const [effectList, seteffectList] = useState([]);
    const [abiliList, setabiliList]= useState([]);
    useEffect(()=>{
        const extractedAbilities = abilities.slice(0,2).map((ability) => ability["ability"]["name"]);
        setabiliList(extractedAbilities);
    },[])
    useEffect(()=>{
        const effecturls = abilities.slice(0,2).map((ability)=>ability["ability"]["url"]);
        const fetchdata = async ()=>{
            const result = await Promise.all(
                effectList.map((url)=>{
                    fetch(url).then(response=>response.json())
                })
            )

            seteffectList(result);
        }

        if(effecturls > 0)
        {
            fetchdata();
        }
    },[])

    console.log(effectList)
    
    return(
        <div className="PokeCard">
            <div className="card-top">
                <div className="card-design-ele">
                    ðŸ’£
                </div>
                <div className="name">
                    <p>{props.name}</p>
                </div>
                <div className="hp">
                    <p>hp: {props.hp}</p>
                </div>
            </div>
            <div className="card-img">
                <img className="image" src={props.imagelink} alt="new"/>
            </div>
            <div className="abilities">
                {abiliList.map((ele,i)=>{
                    return(
                        <div className='ability'>
                        {ele}    
                        <div className="effect">
                            {effectList[i]}
                        </div>
                        </div>
                    )
                })}
            </div>
        <div className="nature-description">
                
        </div>
    </div>
    )
}
