import './App.css';
import PokeCard from './components/PokeCard';
import {useState,useEffect} from 'react';

function App() {
  const [pokeList, setpokeList] = useState([]);
  const [nameList, setnameList] = useState([]);
  const [urlList, seturlList] = useState([]);
  const[hpList, sethpList] = useState([]);

  useEffect(()=>{
      fetch("https://pokeapi.co/api/v2/pokemon")
      .then((response)=>response.json())
      .then((data)=>{
        setpokeList(data["results"]);
      })
  },[])
  useEffect(() => {
    if (pokeList.length !== 0) {
      const names = pokeList.map((ele) => ele["name"]);
      setnameList(names);
      const urls= pokeList.map((ele) => ele["url"]);
      seturlList(urls);
    }
  }, [pokeList]);
  console.log(urlList);
  const [searchquery, setsearchquery] = useState("");
  let cardsData = ["hello", "world"];
  const searchQuery = ()=>{
      cardsData.forEach((element, i)=>{
          if(element === searchquery)
          {
            console.log(i);
          }
      })
  }
  return (
    <div className="App">
      <div className="search-bar">
        âš½ PokeMon Cards
          <div className="search-holder">
              <input className="inpt" type="text" onChange={(e)=>{setsearchquery(e.target.value)}} placeholder="search bar"/>
              <button className="button" onClick={searchQuery} name="search">search</button>
          </div>
      </div>
      <div className="body">
        <div className="cards-container">
          {nameList.map((ele,i)=>{
              return(
                <PokeCard name={ele} hp="40" imagelink="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/showdown/1.gif" abilities={["left","right"]}/>
              )
          })}

        </div>
      </div>
    </div>
  );
}

export default App;
