          <div className="search-holder">
              <input className="inpt" type="text" onChange={(e)=>{setsearchquery(e.target.value)}} placeholder="search bar"/>
              <button className="button" onClick={searchQuery} name="search">search</button>
          </div>
      </div>
      <div className="body">
        <div className="cards-container">
         <PokeCard name="bulbasaur" hp="40" imagelink="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/showdown/1.gif" abilities={["left","right"]}/>
