import './PokeCard.css'
import{useEffect, useState} from 'react';

export default function PokeCard(props){
    const name = props.name;
    const hp = props.hp;
    const imagelink = props.imagelink;
    const abilities = props.abilities;
    const type = props.type;
    
const cardStyles =({
    fire: {
        background:"linear-gradient(to bottom right, red,yellow)"
    },
    grass: {
        "image": "../../grass.jpg",
        "colour":"linear-gradient(to bottom right, lightgreen, lightblue)"
    },
    water: {
        "image": "../../water.jpg",
        "colour":"linear-gradient(to bottom right, lightred, lightbrown)"
    },
    electric: {
        "image": "../../lightning.jpg",
        "colour":"linear-gradient(to bottom right, black, lightyellow)"
    }
})

    const [effectList, seteffectList] = useState([]);
    const [abiliList, setabiliList]= useState([]);
    
    useEffect(()=>{
        const extractedAbilities = abilities.slice(0,2).map((ability) => ability["ability"]);
        setabiliList(extractedAbilities);
    },[])

    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await Promise.all(
                abiliList.map((ele)=> fetch(ele["url"]).then(response=>response.json()))
            )

            seteffectList(result.map((ele,i)=>ele["effect_entries"][i]["effect"]));
        }

        if(abiliList.length > 0)
        {
            fetchdata();
        }
    }, [abiliList])
   
    
    return(
        <div className="PokeCard" style={cardStyle["fire"]}>
            <div className="card-top">
                <div className="card-design-ele">
                    ðŸ’£
                </div>
                <div className="name">
                    <p>{props.name}</p>
                </div>
                <div className="hp">
                    <p>hp: {props.hp}</p>
                </div>
            </div>
            <div className="card-img">
                <img className="image" src={props.imagelink} alt="new"/>
            </div>
            <div className="abilities">
                {abiliList.map((ele,i)=>{
                    return(
                        <div key={`${ele} - ${i}`}className='ability'>
                            <p style={{fontSize:9, fontWeight:'bold'}}>{ele["name"]}</p> 
                           <p style={{fontSize: 5}} align="justify"> {effectList[i]}</p>
                        </div>
                    )
                })}
            </div>
        <div className="nature-description">
                
        </div>
    </div>
    )
}
